import React from "react";
import {
  Container,
  Icon,
  Link,
  List,
  ListItem,
  Offcanvas,
  OffcanvasContainer,
  Navbar,
  NavItem,
  NavbarContainer,
  NavbarSticky,
  Section,
} from "uikit-react";
import Icons from "uikit/dist/js/uikit-icons";

function NavTabs({ currentPage, handlePageChange }) {
  const pageNames = ["Home", "About", "Profile", "Contact"];

  // This function returns jsx for a nav-item for a given page.
  const renderNavTab = (page) => {
    const href = `#${page.toLowerCase()}`;
    const className = currentPage === page ? "nav-link active" : "nav-link";
    const onClick = () => handlePageChange(page);

    return (
      <NavbarContainer>
        <Navbar left>
          <NavItem  key={page}>
            <Link href={href} onClick={onClick} className={className}>
          {page}</Link>
          </NavItem>
        </Navbar>
      </NavbarContainer>


    );
  };

  return (
    <ul className="nav nav-tabs">
      {pageNames.map((page) => renderNavTab(page))}
    </ul>
  );
}

export default NavTabs;
