import React, { Component } from "react";
import { Menu, Segment, Card, Image, Icon } from "semantic-ui-react";

export default class Projects extends Component {
  state = {
    pages: {
      whatsup: {
        topic: "what's up there?",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
      dayout: {
        topic: "A Day Out",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
      weather: {
        topic: "Weather Dashboard",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
      burger: {
        topic: "Burger Devour",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
      notes: {
        topic: "Note Taker",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
      planner: {
        topic: "Daily Planner",
        about: "An application that finds the satellites in orbit above you and allows you to track your favorites."
      },
    },
  };

  handleItemClick = (e, { name , title}) => this.setState({ activeItem: name, pages: title});

  render() {
    const { activeItem } = this.state;

    return (
      <div>
        <div>
          <Menu size="large" pointing secondary inverted vertical>
            <Menu.Item
              className="projects"
              title="whatsup"
              name="WHATS UP THERE"
              active={activeItem === "WHATS UP THERE"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="dayout"
              name="A DAY OUT"
              active={activeItem === "A DAY OUT"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="weather"
              name="WEATHER DASHBOARD"
              active={activeItem === "WEATHER DASHBOARD"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="burger"
              name="BURGER DEVOUR"
              active={activeItem === "BURGER DEVOUR"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="notes"
              name="NOTE TAKER"
              active={activeItem === "NOTE TAKER"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="planner"
              name="WORK PLANNER"
              active={activeItem === "WORK PLANNER"}
              onClick={this.handleItemClick}
            />
          </Menu>
        </div>
        <>
          <Card>
            {/* <Image
              src={}
              wrapped
              ui={false}
            /> */}
            <Card.Content>
        <Card.Header>{this.state.pages}</Card.Header>
              <Card.Meta>
                <span className="date">Joined in 2015</span>
              </Card.Meta>
              <Card.Description>
                Matthew is a musician living in Nashville.
              </Card.Description>
            </Card.Content>
            <Card.Content extra>
              <a>
                <Icon name="user" />
                22 Friends
              </a>
            </Card.Content>
          </Card>
        </>
      </div>
    );
  }
}
