import React, { Component } from "react";
import "./style.css";
import { Form, Input, TextArea, Button, Message } from "semantic-ui-react";
import { IconContext } from "react-icons";
import { BiMailSend } from "react-icons/bi";

class FormExampleCaptureValues extends Component {
  state = {
    name: "",
    email: "",
    message: "",
    submissionMessage: "Looking forward to chatting soon",
  };

  handleChange = (e, { name, value }) => this.setState({ [name]: value });

  handleSubmit = () => {
    const { name, email, message, submissionMessage } = this.state;
    var submission = submissionMessage + " " + name + "!";
    this.setState({ submissionMessage: submission });
    this.setState({
      email: "",
      name: "",
      message: "",
      submissionMessage: "Looking forward to chatting soon",
    });
  };

  render() {
    const { name, email, message, submissionMessage } = this.state;

    return (
      <Form
        autocomplete="on"
        onSubmit={this.handleSubmit}
        className="formF"
        inverted
      >
        <Form.Group to="address@gmail.com" widths="equal">
          <Form.Field
            type="text"
            name="name"
            className="form"
            id="form-input-control-first-name"
            control={Input}
            label="Name"
            value={name}
            onChange={this.handleChange}
          />
          <Form.Field
            type="text"
            name="mail"
            className="form"
            id="form-input-control-email"
            control={Input}
            label="E-mail"
            value={email}
            onChange={this.handleChange}
          />
        </Form.Group>
        <Form.Field
          type="text"
          name="comment"
          className="form"
          id="form-textarea-control-opinion"
          control={TextArea}
          label="Message"
          rows="8"
          value={message}
          onChange={this.handleChange}
        />
        <Message success header="Submitted." content={submissionMessage} />
        <Form.Field control={Button}>
          <IconContext.Provider value={{ size: 30 }}>
            <BiMailSend />
          </IconContext.Provider>{" "}
        </Form.Field>
      </Form>
    );
  }
}

export default FormExampleCaptureValues;
