import React, { Component } from "react";
import { Grid, Container } from "semantic-ui-react";
import PageTitle from "../../PageTitle/PageTitle";
import skillsTitle from "./images/skills.png";
import "./style.css";
import { IconContext } from "react-icons";
import {
  FaPhp,
  FaReact,
  FaCss3Alt,
  FaHtml5,
  FaWordpress,
  FaGitAlt,
  FaGithub,
  FaSalesforce,
  FaBootstrap,
  FaMailchimp,
  FaUikit,
} from "react-icons/fa";
import {
  SiJavascript,
  SiJquery,
  SiVisualstudio,
  SiTailwindcss,
  SiGutenberg,
  SiAlpinedotjs,
  SiZapier,
  SiPhpmyadmin,
} from "react-icons/si";
import { GrMysql } from "react-icons/gr";
import AOS from "aos";
import "aos/dist/aos.css";
import jQuery from "jquery";

let skills = [
  ["PHP", "WordPress", "HTML5", "CSS3", "React"],
  ["JavaScript", "Git", "GitHub", "phpMyAdmin", "MySQL"],
  ["JQuery", "VSCode", "Gutenberg", "Bootstrap", "Tailwind"],
  ["Alpine", "UIkit", "Zapier", "Mailchimp", "Salesforce"],
];

export default class Skills extends Component {
  state = {
    activeItem: "PHP",
  };

  componentDidMount() {
    AOS.init();
  }

  handleItemHoverEnter = (skill) => {
    this.setState({ activeItem: skill.skill });
    this.renderProjects(skill.skill);
    jQuery("#skills .projectRelation").css("opacity", "1");
  };

  handleItemHoverLeave = (skill) => {
    jQuery("#skills .projectRelation").css("opacity", "0");
  };

  renderProjects = (skill) => {
    switch (skill) {
      case "PHP":
        return (
          <div className="projects">
            <span> Agent Elite </span>
            <span> Elite Informer</span>
          </div>
        );
        break;
      default:
        break;
    }
  };

  renderIcon = (title) => {
    switch (title) {
      case "PHP":
        return <FaPhp />;
      case "WordPress":
        return <FaWordpress />;
      case "HTML5":
        return <FaHtml5 />;
      case "CSS3":
        return <FaCss3Alt />;
      case "React":
        return <FaReact />;
      case "JavaScript":
        return <SiJavascript />;
      case "Git":
        return <FaGitAlt />;
      case "GitHub":
        return <FaGithub />;
      case "phpMyAdmin":
        return <SiPhpmyadmin />;
      case "MySQL":
        return <GrMysql />;
      case "JQuery":
        return <SiJquery />;
      case "VSCode":
        return <SiVisualstudio />;
      case "Gutenberg":
        return <SiGutenberg />;
      case "Bootstrap":
        return <FaBootstrap />;
      case "Tailwind":
        return <SiTailwindcss />;
      case "Alpine":
        return <SiAlpinedotjs />;
      case "UIkit":
        return <FaUikit />;
      case "Zapier":
        return <SiZapier />;
      case "Alpine":
        return <SiAlpinedotjs />;
      case "Mailchimp":
        return <FaMailchimp />;
      case "Salesforce":
        return <FaSalesforce />;
      default:
        break;
    }
  };

  render() {
    const { activeItem } = this.state;

    return (
      <div>
        <Container id="skills">
          <div className="wrapper">
            <PageTitle title="My" secondImage={skillsTitle} />
            <div className="projectRelation">
              <span className="experience"> EXPERIENCE </span>
              {this.renderProjects(activeItem)}
            </div>
            <div
              class="skillsGrid"
              data-aos="fade-down"
              data-aos-duration="1000"
              data-aos-delay="200"
            >
              <Grid columns="five" divided>
                {skills.map((row) => {
                  return (
                    <Grid.Row>
                      {row.map((skill) => {
                        return (
                          <Grid.Column>
                            <div
                              class="skill"
                              name={skill}
                              onMouseEnter={this.handleItemHover({ skill })}
                              onMouseLeave={this.handleItemHoverLeave()}
                            >
                              <IconContext.Provider value={{ size: 70 }}>
                                {this.renderIcon(skill)}
                              </IconContext.Provider>
                              <p>{skill}</p>
                            </div>
                          </Grid.Column>
                        );
                      })}
                    </Grid.Row>
                  );
                })}
              </Grid>
            </div>
          </div>
        </Container>
      </div>
    );
  }
}
