import * as React from "react";
import "./style.css";
import { Menu } from 'semantic-ui-react'

function NavTabs({ currentPage, handlePageChange }) {
  const pageNames = ["Home", "About", "Portfolio", "Contact"];

  // This function returns jsx for a nav-item for a given page.
  const renderNavTab = (page) => {
    const href = `#${page.toLowerCase()}`;
    const className = currentPage === page ? "active" : "muted";
    const onClick = () => handlePageChange(page);

    return (
      <NavItem key={page}>
        <Margin type="xlarge-right" />
        <a href={href} onClick={onClick} className={className}>
          {page}
        </a>
      </NavItem>
    );
  };

  return (
    <NavbarSticky options="animation: uk-animation-slide-top; cls-inactive: uk-navbar-transparent uk-dark; top: 556;">
      <NavbarContainer>
        <Navbar right>{pageNames.map((page) => renderNavTab(page))}</Navbar>
        <Margin type="xlarge-right" />
      </NavbarContainer>
    </NavbarSticky>
  );
}

export default NavTabs;
