import React, { Component } from "react";
import { Grid, Image, Icon, Card, Divider } from "semantic-ui-react";
import AOS from "aos";
import charlotte from "./charlotte.jpg";
import "aos/dist/aos.css";

export default class Profile extends Component {
  componentDidMount() {
    AOS.init();
  }

  render() {
    return (
      <div id="profile" data-aos="fade-right">
        <h1 className="aboutTitle">ABOUT</h1>
        <Grid className="gridAbout">
          <Grid.Column width={4} relaxed="very">
            <Image src={charlotte} />
          </Grid.Column>
          <Grid.Column width={9}>
            <Card fluid raised className="cardAbout">
              <h2 className="SD">
                <Icon name="map pin" size="small" />
                San Diego
              </h2>
              <Divider inverted />
              <p className="cardAbout">
                Yes, my actual last name is Mountain. It can be pretty steep,
                but luckily I enjoy dad jokes. I am a southern california
                native, currently residing with my fianc√© and our cat. Pursuing
                the dream of becoming a Web Developer leveraging a background in
                customer service to build a more intuitive user experience on
                the web. Just trying to be the best cat mom I can be.
                <br />
                <br />
                Recently earned a Certificate in Full Stack Development from the
                University of California San Diego Extension, with developed
                skills in JavaScript, CSS, Node.js, JQuery, MySQL and responsive
                web design. With each project, my aim is to best engage my
                audience for an impactful user experience. Excited to leverage
                my skills in the future as part of a fast-paced, quality-driven
                team!
              </p>
            </Card>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
