import * as React from 'react';
import { 
    Container,
    Icon,
    Link,
    List,
    ListItem,
    Offcanvas,
    OffcanvasContainer,
    Navbar,
    NavbarContainer,
    NavbarSticky,
    Section,
} from 'uikit-react';

function NavTabs({ currentPage, handlePageChange }) {
  const pageNames = ["Home", "About", "Blog", "Contact"];

  // This function returns jsx for a nav-item for a given page.
  const renderNavTab = (page) => {
    const href = `#${page.toLowerCase()}`;
    const className = currentPage === page ? "nav-link active" : "nav-link";
    const onClick = () => handlePageChange(page);

    return (
      <li className="nav-item" key={page}>
        <a href={href} onClick={onClick} className={className}>
          {page}
        </a>
      </li>
    );
  };

  return (
    <OffcanvasContainer>
    <Section>
        <NavbarSticky options="animation: uk-animation-slide-top; cls-inactive: uk-navbar-transparent uk-light; top: 556;">
            <NavbarContainer>
                <Navbar>
                    <ListItem>
                        <Link toggleOptions="target: #menu;" href="#">
                            <Icon options="menu" button />
                        </Link>
                    </ListItem>
                </Navbar>
            </NavbarContainer>
        </NavbarSticky>
        <Offcanvas id="menu" options="overlay: true">
            <List type="divider">
                <ListItem>
                    <Link href="/">Home</Link>
                </ListItem>
            </List>
        </Offcanvas>
    </Section>
</OffcanvasContainer>
  );
}

export default NavTabs;
