import React, { Component } from 'react'
import { Menu } from 'semantic-ui-react'

export default class Projects extends Component {
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Menu
      size="large"
       pointing secondary vertical>
        <Menu.Item
        className="projects"
          name="WHATS UP THERE"
          active={activeItem === "what's up there?"}
          onClick={this.handleItemClick}
        />
        <Menu.Item
                className="projects"
          name='A DAY OUT'
          active={activeItem === 'A DAY OUT'}
          onClick={this.handleItemClick}
        />
        <Menu.Item
                className="projects"
          name='WEATHER DASHBOARD'
          active={activeItem === 'WEATHER DASHBOARD'}
          onClick={this.handleItemClick}
        />
         <Menu.Item
                 className="projects"
          name='BURGER DEVOUR'
          active={activeItem === 'BURGER DEVOUR'}
          onClick={this.handleItemClick}
        />
           <Menu.Item
                   className="projects"
          name='NOTE TAKER'
          active={activeItem === 'NOTE TAKER'}
          onClick={this.handleItemClick}
        />
           <Menu.Item
                   className="projects"
          name='WORK PLANNER'
          active={activeItem === 'WORK PLANNER'}
          onClick={this.handleItemClick}
        />
      </Menu>
    )
  }
}
