import * as React from 'react';
import { 
    Container,
    Icon,
    Link,
    List,
    ListItem,
    Offcanvas,
    OffcanvasContainer,
    Navbar,
    NavbarContainer,
    NavbarSticky,
    Section,
    NavItem,
} from 'uikit-react';

{/* <NavbarContainer> <Navbar left> <NavItem> <Link href="#">Link</Link> </NavItem> <NavItem> <Link href="#">Link</Link> </NavItem> <NavItem> <Link href="#">Link</Link> </NavItem> </Navbar> </NavbarContainer>
Dropdown in Navbar */}

function NavTabs({ currentPage, handlePageChange }) {
  const pageNames = ["Home", "About", "Blog", "Contact"];

  // This function returns jsx for a nav-item for a given page.
  const renderNavTab = (page) => {
    const href = `#${page.toLowerCase()}`;
    const className = currentPage === page ? "nav-link active" : "nav-link";
    const onClick = () => handlePageChange(page);

    return (
      <NavItem key={page}>
        <a href={href} onClick={onClick} className={className}>
          {page}
        </a>
      </NavItem>
    );
  };

  return (
    <ul className="nav nav-tabs">
      {pageNames.map((page) => renderNavTab(page))}
    </ul>
  );
}

export default NavTabs;
