import React, { Component } from "react";
import satellite from "./images/sat.jpg";
import met from "./images/met.jpg";
import weatherimg from "./images/weather.jpg";
import burger from "./images/burger.jpg";
import note from "./images/note.jpg";
import work from "./images/work.jpg";
import satelliteGif from "./gifs/whatsup.gif"
import { Menu, Modal, Grid, Card, Button, Image, Icon } from "semantic-ui-react";

let pages = {
  whatsup: {
    topic: "what's up there?",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
    source: satellite,
    gif: satelliteGif,
    github: "https://github.com/charrmountain/whats-up-there/",
    href: "https://whats-up-there.herokuapp.com/",
  },
  dayout: {
    topic: "day out",
    about:
      "An arts education application utilizing the Metropolitan Museum of Art's collections.",
    source: met,
    gif: metGif,
    github: "https://github.com/charrmountain/a-day-out/",
    href: "https://charrmountain.github.io/a-day-out/",
  },
  weather: {
    topic: "weather dashboard",
    about:
      "Weather dashboard that will retrieve a city's current weather and forecast from the user's choice.",
    source: weatherimg,
    gif: weatherGif,
    github: "https://github.com/charrmountain/weather-forecast/",
    href: "https://charrmountain.github.io/weather-forecast/",
  },
  burger: {
    topic: "burger devour",
    about:
      "An entertaining application that allows the user to create and devour burgers.",
    source: burger,
    gif: burgerGif,
    github: "https://github.com/charrmountain/burger/",
    href: "https://evening-thicket-44264.herokuapp.com/",
  },
  notes: {
    topic: "note taker",
    about: "An application that can be used to write, save, and delete notes.",
    source: note,
    gif: noteGif,
    github: "https://github.com/charrmountain/note-taker/",
    href: "https://blooming-forest-27574.herokuapp.com/",
  },
  planner: {
    topic: "daily planner",
    about:
      "A daily work planner that displays hourly storable sections that changes color to display past, current, and future hours.",
    source: work,
    gif: workGif,
    github: "https://github.com/charrmountain/work-day-scheduler/",
    href: "https://charrmountain.github.io/work-day-scheduler",
  },
};

export default class Projects extends Component {
  state = {
    activeItem: "WHATS UP THERE",
    currentPage: pages.whatsup,
  };

  handleItemClick = (e, { name, title }) =>
    this.setState({ activeItem: name, currentPage: pages[title] });

  render() {
    const { activeItem } = this.state;

    return (
      <div>
        <Grid className="ticker" divided="vertically">
          <Grid.Row columns={2} className="ticker">
            <Grid.Column className="ticker">
              <Menu
                size="large"
                pointing
                secondary
                inverted
                vertical
                className="ticker"
              >
                <Menu.Item
                  className="projects"
                  title="whatsup"
                  name="WHATS UP THERE"
                  active={activeItem === "WHATS UP THERE"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="dayout"
                  name="DAY OUT"
                  active={activeItem === "DAY OUT"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="weather"
                  name="WEATHER DASHBOARD"
                  active={activeItem === "WEATHER DASHBOARD"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="burger"
                  name="BURGER DEVOUR"
                  active={activeItem === "BURGER DEVOUR"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="notes"
                  name="NOTE TAKER"
                  active={activeItem === "NOTE TAKER"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="planner"
                  name="WORK PLANNER"
                  active={activeItem === "WORK PLANNER"}
                  onClick={this.handleItemClick}
                />
              </Menu>
            </Grid.Column>
            <Grid.Column>
              <Card fluid raised className="cardProjects">
                <Image
                  className="projectImg"
                  wrapped
                  ui={false}
                  src={this.state.currentPage.source}
                />
                <Card.Content>
                  <Card.Header>
                    <a
                      className="projectTitle"
                      href={this.state.currentPage.href}
                    >
                      {this.state.currentPage.topic}
                    </a>
                  </Card.Header>
                  <Card.Description className="topic">
                    {this.state.currentPage.about}
                    <br />
                    <br />
                    <Icon name="github" size="large" />
                    <a
                      className="gitHubPort"
                      href={this.state.currentPage.github}
                    >
                      GitHub
                    </a>
                    <Modal
                      size="small"
                      trigger={<p className="playPort">DEMO</p>}
                      closeIcon
                    >
                      <Modal.Content>
                        <Image
                          wrapped
                          ui={false}
                          src={this.state.currentPage.gif}
                        />
                      </Modal.Content>
                      <Modal.Actions></Modal.Actions>
                    </Modal>
                  </Card.Description>
                </Card.Content>
              </Card>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}
