import React, { Component } from "react";
import "./style.css";

class Scroll extends Component {
  state = {
    currentPage: "HOME",
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    return (
      <div class="provoke-scroll-bottom is-active">
        <div class="fill">
          <div class="inner" style="top: 99.9977%;"></div>
        </div>
        <div class="text">
          <p class="tag">SCROLL</p>
        </div>
      </div>
    );
  }
}

export default Scroll;
