import * as React from "react";
import "./style.css";
import { Menu} from "semantic-ui-react";

function NavTabs({ currentPage, handlePageChange }) {
  const pageNames = ["HOME", "ABOUT", "PORTFOLIO", "CONTACT"];

  // This function returns jsx for a nav-item for a given page.
  const renderNavTab = (page) => {
    const href = `#${page.toLowerCase()}`;
    const className = currentPage === page ? "active" : "muted";
    const onClick = () => handlePageChange(page);

    return (
<>
      <Menu.Item key={page}
      href={href}
      onClick={onClick} className={className}>
          {page} 
      </Menu.Item>
      <Menu.Menu position='right'>
        <Menu.Item
          name='logout'
          active={activeItem === 'logout'}
          onClick={this.handleItemClick}
        />
      </Menu.Menu>
      </>

    );
  };

  return <Menu
  size='massive' inverted pointing secondary borderless color="white" horizontal className="MenuStyles">{pageNames.map((page) => renderNavTab(page))}</Menu>;
}

export default NavTabs;
