import React, { Component } from "react";
import { Menu, Segment, Card, Image, Icon } from "semantic-ui-react";

export default class Projects extends Component {
  state = {
    activeItem: "portfolio",
    pages: {
      whatsup: {
        title: "what's up there?",
      },
    },
  };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;

    return (
      <div>
        <div>
          <Menu size="large" pointing secondary inverted vertical>
            <Menu.Item
              className="projects"
              title="whatsup"
              name="WHATS UP THERE"
              active={activeItem === "WHATS UP THERE"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="dayout"
              name="A DAY OUT"
              active={activeItem === "A DAY OUT"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="weather"
              name="WEATHER DASHBOARD"
              active={activeItem === "WEATHER DASHBOARD"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="burger"
              name="BURGER DEVOUR"
              active={activeItem === "BURGER DEVOUR"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="notes"
              name="NOTE TAKER"
              active={activeItem === "NOTE TAKER"}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              className="projects"
              title="planner"
              name="WORK PLANNER"
              active={activeItem === "WORK PLANNER"}
              onClick={this.handleItemClick}
            />
          </Menu>
        </div>
        <>
          <Card>
            <Image
              src="https://react.semantic-ui.com/images/avatar/large/matthew.png"
              wrapped
              ui={false}
            />
            <Card.Content>
              <Card.Header>Matthew</Card.Header>
              <Card.Meta>
                <span className="date">Joined in 2015</span>
              </Card.Meta>
              <Card.Description>
                Matthew is a musician living in Nashville.
              </Card.Description>
            </Card.Content>
            <Card.Content extra>
              <a>
                <Icon name="user" />
                22 Friends
              </a>
            </Card.Content>
          </Card>
        </>
      </div>
    );
  }
}
