import React, { Component } from "react";
import { Menu, Segment, Grid, Card, Image, Icon } from "semantic-ui-react";

let pages = {
  whatsup: {
    topic: "what's up there?",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
  dayout: {
    topic: "A Day Out",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
  weather: {
    topic: "Weather Dashboard",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
  burger: {
    topic: "Burger Devour",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
  notes: {
    topic: "Note Taker",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
  planner: {
    topic: "Daily Planner",
    about:
      "An application that finds the satellites in orbit above you and allows you to track your favorites.",
  },
};

export default class Projects extends Component {
  state = {
    activeItem: "portfolio",
    currentPage: pages.whatsup,
  };

  handleItemClick = (e, { name, title }) =>
    this.setState({ activeItem: name, currentPage: pages[title] });

  render() {
    const { activeItem } = this.state;

    return (
      <div>
        <Grid divided="vertically">
          <Grid.Row columns={2}>
            <Grid.Column>
              <Menu size="large" pointing secondary inverted vertical>
                <Menu.Item
                  className="projects"
                  title="whatsup"
                  name="WHATS UP THERE"
                  active={activeItem === "WHATS UP THERE"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="dayout"
                  name="A DAY OUT"
                  active={activeItem === "A DAY OUT"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="weather"
                  name="WEATHER DASHBOARD"
                  active={activeItem === "WEATHER DASHBOARD"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="burger"
                  name="BURGER DEVOUR"
                  active={activeItem === "BURGER DEVOUR"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="notes"
                  name="NOTE TAKER"
                  active={activeItem === "NOTE TAKER"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  className="projects"
                  title="planner"
                  name="WORK PLANNER"
                  active={activeItem === "WORK PLANNER"}
                  onClick={this.handleItemClick}
                />
              </Menu>
            </Grid.Column>
            <Grid.Column>
              <Card>
                {/* <Image
              src={}
              wrapped
              ui={false}
            /> */}
                <Card.Content>
        <Card.Header>{this.state.currentPage.topic}</Card.Header>
                  <Card.Description>
                  {this.state.currentPage.about}
                  </Card.Description>
                </Card.Content>
                <Card.Content extra>
                  <a>
                    <Icon name="user" />
                    22 Friends
                  </a>
                </Card.Content>
              </Card>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}
